mapper.select:
select * from (
	select rownum rn,t.* 
	from (
		select * from message 
		where 1=1

			and id>#{gtid}


			and id<#{ltid}


			and id=#{id}


			and username=#{username}


			and title like '%${title}%'


			and context like '%${context}%'


			and delivertime >= to_date(#{gt_delivertime}, 'yyyy-mm-dd')


			and delivertime <= to_date(#{lt_delivertime}, 'yyyy-mm-dd')


			and ip like'%${ip}%'

		) t
	where rownum<=#{page.currentPage}*#{page.countPerPage} )
where rn > (#{page.currentPage}-1) * #{page.countPerPage}
